import { useState } from 'react';
import './App.css';
import axios from 'axios';
import {
  ChakraProvider,
  Container,
  VStack,
  Button,
  Heading,
  Text,
  Input,
  Spinner,
} from '@chakra-ui/react'

const BACKEND_URL = "http://localhost:8000";

function App() {
  const [prompt, setPrompt] = useState('');
  const handleChange = (event) => setPrompt(event.target.value);
  const [output, setOutput] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSearch = () => {
    setLoading(true);
    axios
    .get(BACKEND_URL + "/papers?prompt=" + prompt)
    .then((res) => {
      setOutput(res.data);
    })
    .catch((err) => {
      console.log(err);
    })
    .finally(() => {
      setLoading(false);
    });
  }

  return (
    <ChakraProvider>
      <Heading size="lg" textAlign="center">
        ResearchGPT
      </Heading>
      <Heading size="md" textAlign="center">
        Built using LlamaIndex
      </Heading>
      <Container maxW="600px">
        <VStack>
          <Text>
            What research topics or concepts are you looking for?
          </Text>
          <Input
            value={prompt}
            onChange={handleChange}
            placeholder="e.g. reinforcement learning in music"
            size="md"
          />
          <Button onClick={handleSearch}>
            Search
          </Button>
          {loading ? <Spinner/> : <Text>{output}</Text>}
        </VStack>
      </Container>
    </ChakraProvider>
  );
}

export default App;
